import Link from 'next/link';
import { withRouter } from 'next/router';

function Navbar({ router }) {
  const navs = [
    { text: 'Home', href: '/' },
    { text: 'Portfolio', href: '/portfolio' },
    { text: 'About', href: '/about' },
    { text: 'Contact', href: '/contact' },
  ];

  return (
    <div>
      <nav className="navbar">
        <div className="container">
          <Link href="/"><a className="logo">LV</a></Link>

          <ul className="nav-links">
            {navs.map(nav => (
              <li><Link href={nav.href}><a className={`nav-item ${router.pathname == nav.href ? 'active' : ''}`}>{nav.text}</a></Link></li>
            ))}
          </ul>
        </div>
      </nav>
      <style jsx>
        {`
        a[href] {
          color: #000;
        }
        
        .container {
          width: 980px;
          margin-left: auto;
          margin-right: auto;
        }
        
        .navbar {
          margin-top: 20px;
        }
        
        .navbar .container {
          display: flex;
          align-items: center;
        }
        
        .navbar .logo {
          font-size: 28px;
          text-transform: uppercase;
          letter-spacing: 3px;
          font-weight: 500;
          text-decoration: none;
        }
        
        .navbar .nav-links {
          display: flex;
          margin-left: auto;
          list-style: none;
        }
        
        .navbar .nav-links .nav-item {
          position: relative;
          display: inline-block;
          margin-left: 20px;
          margin-right: 20px;
          text-decoration: none;
          transition: opacity 0.3s;
        }
        
        .navbar .nav-links .nav-item.active {
          font-weight: 700;
        }
        
        .navbar .nav-links .nav-item:hover {
          opacity: 0.5;
        }
        `}
      </style>
    </div>
  );
}

export default withRouter(Navbar);
